#show databases;#use test;#show tables;# Create users TableCREATE TABLE `users` (	`user_id` INT(40) NOT NULL AUTO_INCREMENT,	`email_id` VARCHAR(80) NOT NULL,	`password` VARCHAR(80) NOT NULL,	`first_name` VARCHAR(80),	`last_name` VARCHAR(80),	UNIQUE KEY `email_id_Index` (`email_id`) USING BTREE,	PRIMARY KEY (`user_id`));# Create institutions TableCREATE TABLE `institutions` (	`institution_id` INT(40) NOT NULL AUTO_INCREMENT,	`institution_name` VARCHAR(80) NOT NULL,	PRIMARY KEY (`institution_name`));# Create roles TableCREATE TABLE `roles` (	`role_id` INT(40) NOT NULL AUTO_INCREMENT,	`role_name` VARCHAR(80) NOT NULL,	PRIMARY KEY (`role_id`));# Create grades TableCREATE TABLE `grades` (	`grade_id` INT(40) NOT NULL AUTO_INCREMENT,	`grade_name` VARCHAR(20) NOT NULL,	PRIMARY KEY (`grade_id`));# Create subjects TableCREATE TABLE `subjects` (	`subject_id` INT(40) NOT NULL AUTO_INCREMENT,	`subject_name` VARCHAR(80) NOT NULL,	PRIMARY KEY (`subject_id`));# Create user_to_role TableCREATE TABLE `user_to_role` (	`user_id` INT(40),	`role_id` INT(40),	PRIMARY KEY (`user_id`,`role_id`),	FOREIGN KEY (user_id)      		REFERENCES users (user_id)      		ON UPDATE CASCADE ON DELETE CASCADE,   	FOREIGN KEY (role_id)      		REFERENCES roles(role_id)		ON UPDATE CASCADE ON DELETE CASCADE);# Create user_to_institution TableCREATE TABLE `user_to_institution` (	`user_id` INT(40),	`institution_id` INT(40),	PRIMARY KEY (`user_id`,`institution_id`),	FOREIGN KEY (user_id)      		REFERENCES users (user_id)      		ON UPDATE CASCADE ON DELETE CASCADE,   	FOREIGN KEY (institution_id)      		REFERENCES institutions(institution_id)		ON UPDATE CASCADE ON DELETE CASCADE);# Create student_to_grade TableCREATE TABLE `student_to_grade` (	`user_id` INT(40),	`grade_id` INT(40),	PRIMARY KEY (`user_id`,`grade_id`),	FOREIGN KEY (user_id)      		REFERENCES users (user_id)      		ON UPDATE CASCADE ON DELETE CASCADE,   	FOREIGN KEY (grade_id)      		REFERENCES grades(grade_id)		ON UPDATE CASCADE ON DELETE CASCADE);# Create syllabus TableCREATE TABLE `syllabus` (	`grade_id` INT(40),	`subject_id` INT(40),	PRIMARY KEY (`grade_id`,`subject_id`),	FOREIGN KEY (grade_id)      		REFERENCES grades (grade_id)      		ON UPDATE CASCADE ON DELETE CASCADE,   	FOREIGN KEY (subject_id)      		REFERENCES subjects(subject_id)		ON UPDATE CASCADE ON DELETE CASCADE);